import { ApiProperty } from "@nestjs/swagger";

export class AuthResponseOtpDto {
  @ApiProperty({
    type: "string",
    description: "User's UUID",
    example: "123e4567-e89b-12d3-a456-426614174000",
    required: true,
  })
  uuid: string;

  @ApiProperty({
    type: "boolean",
    description: "Indicates if 2FA is enabled for the user",
    example: true,
    required: true,
  })
  enabled2Fa: boolean;

  @ApiProperty({
    type: "string",
    description: "QR code for setting up 2FA",
    example:
      "data:image/png;base64,...",
    required: false,
  })
  qr: string;
}
